package com.paloit.kata.model;import java.util.ArrayList;import java.util.Arrays;import java.util.Collections;import java.util.List;public class Facture {    private final List<LigneFacture> ligneFactures;    private final Float pttc;    private final Float montantTaxe;    public Facture() {        ligneFactures = Collections.emptyList();        pttc = 0f;        montantTaxe = 0f;    }    private Facture(List<LigneFacture> ligneFactures, Float pttc, Float montantTaxe) {        this.ligneFactures = ligneFactures;        this.pttc = pttc;        this.montantTaxe = montantTaxe;    }    public List<LigneFacture> getLigneFactures() {        return ligneFactures;    }    public Float getPttc() {        return pttc;    }    public Float getMontantTaxe() {        return montantTaxe;    }    public Facture addLigneFacture(final LigneFacture ligneFacture) {        return new Facture();    }}