package com.paloit.kata.model;import java.math.BigDecimal;import java.math.MathContext;import java.math.RoundingMode;public class LigneFacture {	private final Produit produit;	private final Integer quantite;	private final Float prixHT;	private final Float prixTTC;	public LigneFacture(Produit produit, int quantite) {		this.produit = produit;		this.quantite = quantite;		prixHT = quantite*produit.getPrixUT();		float montantTaxe = new BigDecimal(				produit.getTaxe() * prixHT / 100)				.round(new MathContext(3, RoundingMode.HALF_UP)).floatValue();		prixTTC = prixHT + montantTaxe;	}	public Produit getProduit() {		return produit;	}	public Integer getQuantite() {		return quantite;	}	public Float getPrixHT() {		return prixHT;	}	public Float getPrixTTC() {		return prixTTC;	}}