package com.paloit.kata.services;import com.paloit.kata.model.*;import org.junit.Test;import java.util.HashMap;import java.util.Map;import static org.assertj.core.api.Assertions.assertThat;public class FactureServiceTest {    private final FactureService factureService = FactureService.getInstance();    @Test    public void input1() {        Book book1 = new Book("code", "livre", 12.49f);        Others cd = new Others("CD", "musique", 14.99f);        Food barreChocolat = new Food("code", "Barre de chocolat", 0.85f);        Map<Produit, Integer> panier = new HashMap<>();        panier.put(book1, 2);        panier.put(cd, 1);        panier.put(barreChocolat, 3);        Facture facture = factureService.initFacture(panier);        factureService.calculerFacture(facture);        assertThat(                facture.getPttc()        ).isEqualTo(48.05f);        assertThat(                facture.getMontantTaxe()        ).isEqualTo(5.53f);    }    @Test    public void input2() {        Food barreChocolat = new Food("code", "Barre de chocolat", 10f);        Others parfum = new Others("fragrance", "channel", 47.5f);        Map<Produit, Integer> panier = new HashMap<>();        panier.put(new Imported(barreChocolat), 2);        panier.put(new Imported(parfum), 3);        Facture facture = factureService.initFacture(panier);        factureService.calculerFacture(facture);        assertThat(                facture.getMontantTaxe()        ).isEqualTo(36.65f);        assertThat(                facture.getPttc()        ).isEqualTo(199.15f);    }    @Test    public void input3() {        Medical aspirine = new Medical("code", "aspirine", 9.75f);        Food boiteChocolat = new Food("code", "Barre de chocolat", 11.25f);        Others parfum = new Others("fragrance", "channel", 27.99f);        Others parfum2 = new Others("fragrance", "channel", 18.99f);        Map<Produit, Integer> panier = new HashMap<>();        panier.put(new Imported(parfum), 2);        panier.put(parfum2, 1);        panier.put(aspirine, 3);        panier.put(new Imported(boiteChocolat), 2);        Facture facture = factureService.initFacture(panier);        factureService.calculerFacture(facture);        assertThat(                facture.getMontantTaxe()        ).isEqualTo(18.98f);        assertThat(                facture.getPttc()        ).isEqualTo(145.7f);    }}